import React, { Component } from 'react';
import Nav from 'react-bootstrap/Nav';
import { Row, Col, Container, TabContainer, Tab, Tabs}  from 'react-bootstrap';

import Hint from './hint.jsx';
import Workspace from './workspace.jsx';
import Explain from './explain.jsx';
import Elaborate from './elaborate.jsx';
import MultiMath from './explore.jsx';
import LoadContent from './loadcontent.jsx';

class WorkSpace extends Component{

  constructor(props){
      super(props);
      this.state={
        plusopen: false,
        inputValue:'',
        key: '',
        defaultContent: false
      }

      this.handlePlus = this.handlePlus.bind(this);
  }

componentDidMount(){
  this.setState({
    defaultContent: true
  })
}

componentDidUpdate(previousProps, previousState) {
  if (previousState.key !== this.state.key) {
      this.setState({
        defaultContent: false
      })
  }
}

  handlePlus(){
    this.setState(state => ({
      plusopen: !state.plusopen
    }));
  }

  handleInput(e){
    let inputValue = e.target.value;
  
          if (!Number(inputValue)) {
              return;
          }
          this.setState({
              [e.target.value]: inputValue
          });
  }

  setKey(k){
    this.setState({
      key: k,
    
    })
  }
    render(){
      const {inputValue, defaultContent} = this.state
     
        return(
          
          <div className={'wrkarea'}>
              
            <Tabs id="controlled-tab-example" activeKey={this.state.key}  onSelect={(k) => this.setKey(k)}> 
                <Tab eventKey="hint" title="Hint">
                     <Hint />
                </Tab>
                <Tab eventKey="explore" title="Explore">
                     <MultiMath handlePlus={this.handlePlus} />
                </Tab>
                <Tab eventKey="explian" title="Explian" >
                    <Explain />
                </Tab>
                <Tab eventKey="elaborate" title="Elaborate" >
                    <Elaborate />
                </Tab>
           
         
                <Tab eventKey="Feedback" title="FeedBack">
                  </Tab>
                <Tab eventKey="Chatbots" title="Chatbots">
                 </Tab>
                <Tab eventKey="Ask the teacher" title="Ask the teacher" >
                </Tab>
              
               
                </Tabs>
            { defaultContent ? (<LoadContent />): ''} 
          </div>
        );
    }
};


export default WorkSpace;
