import React, { Component } from 'react';
import Banner  from './components/banner.jsx';
import Question  from './components/question.jsx';
import Tools  from './components/tools.jsx';
import Workspace  from './components/workspace.jsx';
import url from './api/index.json';
import styles from './app.module.css';
//styles
import { Row, Col, Container }  from 'react-bootstrap'


class App extends Component{
  constructor(props){
    super(props);
    this.state = {
        banner: '',
        question: '',
        collapseOpen: false,
        exploreOpen: false,
        counter: 0,
        inputValue:'',
        plusopen: false,
        video: ''
    }
    this.handleHint = this.handleHint.bind(this);
    this.handleExplore = this.handleExplore.bind(this);
    this.handlePlus = this.handlePlus.bind(this);
    this.handleInput = this.handleInput.bind(this);
    this.handleElaborat = this.handleElaborat.bind(this);
  }

 async componentDidMount(){

    const data = await url.data;

    //console.log('data', data);
    this.setState({banner: data.banner, question: data.question })
}

handleHint(){
  this.setState(state => ({
      collapseOpen: !state.collapseOpen
    }));
}

handleExplore(){
  this.setState(state => ({
    exploreOpen: !state.exploreOpen
  }));
}

handlePlus(){
  this.setState(state => ({
    plusopen: !state.plusopen
  }));
}

handleInput(e){
  let inputValue = e.target.value;

        if (!Number(inputValue)) {
            return;
        }
        this.setState({
            [e.target.value]: inputValue
        });
}

handleElaborate(){
  const url= 'https://www.youtube.com/watch?v=bHpH2AqU9tg';
  this.setState({
    video: 'https://www.youtube.com/watch?v=bHpH2AqU9tg'
  })
  }

  render(){


    const { banner, question, collapseOpen,  exploreOpen, counter} = this.state
    return(
      <div className="App">
      <Container>
        <div className={styles.wrapper}>
        <Row>
        <Col md={12}> <Banner banner={banner}/></Col>
        </Row>
        <Row>
          <Question question={question}/>
        </Row>
        <Row>
        <Col md={6}><Workspace exploreOpen={exploreOpen} handlePlus={this.handlePlus} handleInput={this.handleInput} counter={counter}/></Col>
        <Col md={6}><Tools 
        handleHint={this.handleHint} 
        handleExplore={this.handleExplore } 
        collapseOpen={collapseOpen} 
        handleElaborate={this.handleElaborate}
        video={this.state.video} /></Col>
        </Row>
        </div>
     </Container>

    </div>
    );
  }
}

export default App;
