import React, { Component } from 'react';
import Banner  from './components/banner.jsx';
import Question  from './components/question.jsx';
import Tools  from './components/tools.jsx';
import url from './api/index.json';
//styles
import { Row, Col, Container }  from 'react-bootstrap'


class App extends Component{
  constructor(props){
    super(props);
    this.state = {
        banner: '',
        question: '',
        collapseOpen: false,
        exploreOpen: false,
    }
    this.handleHint = this.handleHint.bind(this);
    this.handleExplore = this.handleExplore.bind(this);

  }

 async componentDidMount(){

    const data = await url.data;

    //console.log('data', data);
    this.setState({banner: data.banner, question: data.question })
}

handleHint(){
  this.setState(state => ({
      collapseOpen: !state.collapseOpen
    }));
}

handleExplore(){
  this.setState(state => ({
    exploreOpen: !state.exploreOpen
  }));
}

  render(){

    const { banner, question, collapseOpen } = this.state
    return(
      <div className="App">
      <Container>
        <Row>
           <Banner banner={banner}/>
        </Row>
        <Row>
          <Question question={question}/>
        </Row>
        <Row>
        <Col md={6}>2 of 3 (wider)</Col>
        <Col md={6}><Tools handleHint={this.handleHint} collapseOpen={collapseOpen}/></Col>
        </Row>
     </Container>
    </div>
    );
  }
}

export default App;
