import React, { Component } from 'react';
import Nav from 'react-bootstrap/Nav';
import { Row, Col, Container, TabContainer, Tab}  from 'react-bootstrap';
import Hint from './hint.jsx';
import Workspace from './workspace.jsx';
import Explain from './explain.jsx';
import Elaborate from './elaborate.jsx';
import Explore from './explore.jsx';

class WorkSpace extends Component{

  constructor(){
      super();
      this.state={
        plusopen: false,
        inputValue:'',
      }

      this.handlePlus = this.handlePlus.bind(this);
  }


  handlePlus(){
    this.setState(state => ({
      plusopen: !state.plusopen
    }));
  }

  handleInput(e){
    let inputValue = e.target.value;
  
          if (!Number(inputValue)) {
              return;
          }
          this.setState({
              [e.target.value]: inputValue
          });
  }

    render(){
      const {inputValue} = this.state
        return(
          <div className={'wrkarea'}>
            <Tab.Container id="left-tabs-example" defaultActiveKey="first">
            <Row>
              <Col sm={3}>
                <Nav variant="pills" className="flex-column">
                  <Nav.Item>
                    <Nav.Link eventKey="hint"> Hint</Nav.Link>
                  </Nav.Item>
                  <Nav.Item>
                    <Nav.Link eventKey="Explore">Explore</Nav.Link>
                  </Nav.Item>
                  <Nav.Item>
                    <Nav.Link eventKey="Explain">Explain</Nav.Link>
                  </Nav.Item>
                  <Nav.Item>
                    <Nav.Link eventKey="Elaborate">Elaborate</Nav.Link>
                  </Nav.Item>
                
                </Nav>
              </Col>
              <Col sm={9}>
                <Tab.Content>
                  <Tab.Pane eventKey="hint">
                      <Hint />
                  </Tab.Pane>
                  <Tab.Pane eventKey="Explore">
                   <Explore handlePlus={this.handlePlus} handleInput={this.handleInput} inputValue={inputValue}/>
                  </Tab.Pane>
                  <Tab.Pane eventKey="Explain">
                    <Explain />
                  </Tab.Pane>
                  <Tab.Pane eventKey="Elaborate">
                    <Elaborate />
                  </Tab.Pane>
                </Tab.Content>
              </Col>
            </Row>
          </Tab.Container>
          </div>
        );
    }
};


export default WorkSpace;
