  import React, { Component } from 'react';
  import { Player, ControlBar, ClosedCaptionButton, BigPlayButton  } from 'video-react';

  class Elaborate extends Component{

    constructor(props){
      super(props);
      this.myRef = React.createRef();
      this.state={}
      this.pause = this.pause.bind(this);
    }
    pause() {
     console.log('pause')
    }
  
    onBlur = (e) => {
      if ( !e.currentTarget.contains( e.relatedTarget ) ) {
        console.log('blur event');
      }
    }

   
    render(){
      const value = this.myRef.current;

      console.log('value', value);
   
      return(
        <div>
           <div className={'video-wrap'}>
            <Player videoId="video-1" fluid={false} width={700} height={300}  ref={this.myRef} onBlur={this.onBlur}>
              <source
                src="//d2zihajmogu5jn.cloudfront.net/elephantsdream/ed_hd.mp4"
                type="video/mp4" 
              />
              <BigPlayButton position="center" />
              <ControlBar autoHide={false} pause={true}>
                <ClosedCaptionButton order={7} />
              </ControlBar>
            </Player>
            <a onClick={this.pause} className="mr-3">
            pause()
          </a>
              </div>
        </div>

      );
    }
  };

  export default Elaborate;
