import React, { Component } from 'react';
import Banner  from './components/banner.jsx';
import Question  from './components/question.jsx';
import Tools  from './components/tools.jsx';
import Workspace  from './components/workspace.jsx';
import url from './api/index.json';
//styles
import { Row, Col, Container }  from 'react-bootstrap'


class App extends Component{
  constructor(props){
    super(props);
    this.state = {
        banner: '',
        question: '',
        collapseOpen: false,
        exploreOpen: false,
        counter: 0
    }
    this.handleHint = this.handleHint.bind(this);
    this.handleExplore = this.handleExplore.bind(this);
    this.handlePlus = this.handlePlus.bind(this);

  }

 async componentDidMount(){

    const data = await url.data;

    //console.log('data', data);
    this.setState({banner: data.banner, question: data.question })
}

handleHint(){
  this.setState(state => ({
      collapseOpen: !state.collapseOpen
    }));
}

handleExplore(){
  this.setState(state => ({
    exploreOpen: !state.exploreOpen
  }));
}

handlePlus(){
  this.setState({
    counter: this.state.counter + 1
  })
}


  render(){


    const { banner, question, collapseOpen,  exploreOpen, counter} = this.state
    return(
      <div className="App">
      <Container>
        <Row>
           <Banner banner={banner}/>
        </Row>
        <Row>
          <Question question={question}/>
        </Row>
        <Row>
        <Col md={6}><Workspace exploreOpen={exploreOpen} handlePlus={this.handlePlus} counter={counter}/></Col>
        <Col md={6}><Tools handleHint={this.handleHint} handleExplore={this.handleExplore } collapseOpen={collapseOpen}/></Col>
        </Row>
     </Container>
    </div>
    );
  }
}

export default App;
