import React, { Component } from 'react';
import Banner  from './components/banner.jsx';
import Question  from './components/question.jsx';
import Submission from './components/submission.jsx';
import Workspace  from './components/workspace.jsx';
import WorkSpace from './components/workspacer.jsx';
import Answer from './components/answer.jsx';
import url from './api/index.json';
import './style/style.css';
//styles
import { Row, Col, Container }  from 'react-bootstrap'


class App extends Component{
  constructor(props){
    super(props);
    this.state = {

        question: '',
        collapseOpen: false,
        exploreOpen: false,
        counter: 0,
        inputValue:'',
        plusopen: false,

    }
    this.handleHint = this.handleHint.bind(this);
    this.handleExplore = this.handleExplore.bind(this);
    this.handlePlus = this.handlePlus.bind(this);
    this.handleInput = this.handleInput.bind(this);

  }

 async componentDidMount(){

    const data = await url.data;

    //console.log('data', data);
    this.setState({question: data.question })
}

handleHint(){
  this.setState(state => ({
      collapseOpen: !state.collapseOpen
    }));
}

handleExplore(){
  this.setState(state => ({
    exploreOpen: !state.exploreOpen
  }));
}

handlePlus(){
  this.setState(state => ({
    plusopen: !state.plusopen
  }));
}

handleInput(e){
  let inputValue = e.target.value;

        if (!Number(inputValue)) {
            return;
        }
        this.setState({
            [e.target.value]: inputValue
        });
}


  render(){


    const {question, collapseOpen,  exploreOpen, counter} = this.state
    return(
      <div className="App">
      <Container>
        <div className={'wrapper'}>
        <div className={'banner-content'} width={'100%'}><Banner /></div>
        <div className={'inner-wrapper'}>
        <Row>
        <Col md={12}> 
        <div className={'ques-content'}> <Question question={question}/> </div></Col>
        </Row>

        <Row>
        <Col md={6}><Workspace exploreOpen={exploreOpen} handlePlus={this.handlePlus}
         handleInput={this.handleInput} counter={counter}/></Col>

        </Row>

        <Row>
            <Col  md={12}>
              <WorkSpace />
            </Col>
        </Row>
        <Row>
          <Col md={12}>
         < Answer />
          </Col>
          </Row>
          <Row>
          <Col md={12}>
          <Submission/>
          </Col>
          </Row>
          </div>
        <Row>
        <Col md={12}>
          <div className={'footer'}>
          @CopyRight 2020

        </div> 
        </Col>
        </Row>

      </div> 

     </Container>

    </div>
    );
  }
}

export default App;
