import { MathFieldComponent } from 'react-mathlive';
import MathLive from 'mathlive';


class MultiMath extends Component{

    constructor(){
        super();
        this.state={
            count: 0,
            add: false
        }
    }

    addQuestion = () =>{
        let counter = this.state.count.concat([''])
        this.setState({
            count: counter,
            add: (!this.state.add)
        })
        console.log(count)
        
    }

     handleDelete = i => e => {
        e.preventDefault()
        let newarr = [
          ...this.state.count.slice(0, i),
          ...this.state.count.slice(i + 1)
        ]
        this.setState({
             count: newarr
        })
      }

    render(){
        return(
            <div>
                    
      <div className="App">
          <p>Use Math editor for calculation </p>
        
        {count.slice(0, 4).map((count, index ) => (
        
          <div key={index} className="wrap-set">
        <p>Step:&nbsp;{parseInt(`${index}`) + 1}</p>
         <span onClick={ handleChange}  className={'fieldwrap'}></span>  
         
         {parseInt(index) + 1 ===  1 ?
          <><button onClick={addQuestion} className={'editor-plus btn btn-primary'}>+</button> </>: 
         <><button onClick={handleDelete(index)} className={'editor-plus sub btn btn-primary'}>-</button>
         <button onClick={addQuestion} className={'editor-plus btn btn-primary'}>+</button> </>}
          </div>
        ))  }
     
         <pre id="output"></pre>

      </div>
            </div>
        );
    }
}
export default MultiMath;
