import React, { Component } from 'react';
import { MathFieldComponent } from 'react-mathlive';
import MathLive from 'mathlive';


class MultiMath extends Component{

    constructor(){
        super();
      
        this.state={
            count: [1],
            add: false
        }
    }



    handleClick(e){
     
        const target = e.target;
     
        if (target) {
            const mathField = MathLive.makeMathField(target);
            mathField.$setConfig({
              virtualKeyboardMode: 'manual',
              virtualKeyboards: 'all'
            })
          }
        }

     

    addQuestion = () =>{
        let counter = this.state.count.concat([''])
        this.setState({
            count: counter,
            add: (!this.state.add)
        })
       
        
    }

     handleDelete = i => e => {
        e.preventDefault()
        let newarr = [
          ...this.state.count.slice(0, i),
          ...this.state.count.slice(i + 1)
        ]
        this.setState({
             count: newarr
        })
      }

    render(){
         const { count } = this.state
         
        return(
            <div>
          
      <div className="App">
          <p>Use Math editor for calculation </p>
      { count.length > 0 && count !== 0 ? (count.slice(0, 4).map((count, index ) => (
        
          <div key={index} className="wrap-set">
        <p>Step:&nbsp;{parseInt(`${index}`) + 1}</p>
         <span className={'fieldwrap'}  onClick={(e)=>this.handleClick(e)}></span>  
         
         {parseInt(index) + 1 ===  4 ?  <button onClick={this.handleDelete(index)} className={'editor-plus btn btn-primary'}>-</button>  :

        parseInt(index) + 1 ===  1 ?  <><button onClick={this.addQuestion} className={'editor-plus btn btn-primary'} ref={this.addRef}>+</button> </>: 
          <><button onClick={this.handleDelete(index)} className={'editor-plus sub btn btn-primary'}>-</button>
         <button onClick={this.addQuestion} className={'editor-plus btn btn-primary'}>+</button> </> }
          </div>
        ))  ) : null }
     
         <pre id="output"></pre>

      </div> 
            </div>
        );
    }



     
}
export default MultiMath;
