import React from 'react';
import { Collapse, Button } from 'react-bootstrap';


 const Tools = ({handleHint, collapseOpen, handleExplore}) => {

  const style={
    background: '#2492ff',
    margin: '5px',
    minwidth: '100px', 
    boxShadow: 'inherit',
    collapse:{background: 'rgb(255, 255, 255)',
      padding: '13px',
      marginTop: '12px',
      boxShadow: 'rgba(0, 0, 0, 0.22) 0px 0px 4px inset'},
      
  }
 
   return ( 
<div >
    <Button style={style}
     onClick ={() => handleHint()}
   aria-expanded={collapseOpen}
  >
    Hint
  </Button>

  <Button style={style}
     onClick ={() => handleExplore()}
  >
  Explore
  </Button>
 <Collapse in={collapseOpen}>
    <div id="example-collapse-text" style={style.collapse}>
      Anim pariatur cliche reprehenderit, enim eiusmod high life accusamus
      terry richardson ad squid. Nihil anim keffiyeh helvetica, craft beer
      labore wes anderson cred nesciunt sapiente ea proident.
    </div>
  </Collapse>


  </div>

    )
};

export default Tools;
