  import React, { Component } from 'react';
  import { Player, ControlBar, ClosedCaptionButton, BigPlayButton  } from 'video-react';

  class Elaborate extends Component{

    constructor(props){
      super(props);
      this.state={}
      this.pause = this.pause.bind(this);
    }
    pause() {
     console.log('pause')
    }
  
    componentDidMount() {
      window.addEventListener("focus", this.blur)
  }
    componentWilUnmount() {
      window.removeEventListener("focus", this.blur)
  }

  blur = () => {
  this.pause
}
    render(){
      return(
        <div>
           <div className={'video-wrap'}>
            <Player videoId="video-1" fluid={false} width={700} height={300}  ref={player => {this.player = player;}} onBlur={this.onBlur}>
              <source
                src="//d2zihajmogu5jn.cloudfront.net/elephantsdream/ed_hd.mp4"
                type="video/mp4" 
              />
              <BigPlayButton position="center" />
              <ControlBar autoHide={false} pause={true}>
                <ClosedCaptionButton order={7} />
              </ControlBar>
            </Player>
            <a onClick={this.pause} className="mr-3">
            pause()
          </a>
              </div>
        </div>

      );
    }
  };

  export default Elaborate;
