import React from 'react';

const PollOption = ({options, onChange, selectedValue}) =>{
    return(
        <div>
            {options.map((choice, id)=>{
                <label key={id}>
                    <input type="radio"
                          name="choice"
                          value={choice.id}
                          key={id}
                          checked={selectedValue === choice.id}
                          onChange={(e)=> onChange(e.target.value)} 
                          />
                </label>
            })
            }
        </div>
    );
};

export default PollOption;
